mapper.select:
select se.*,art.ASSIGNEE_ as nextUser,art.ID_ as taskId , if(se.apply_status = -1 or se.apply_status = 7, 2 , if (art.ASSIGNEE_=#{query.applyUser} or art.ASSIGNEE_ is null, 0 , 2 )) as flowStatus from survey_evaluation se left join act_ru_task art on se.process_instance_id = art.PROC_INST_ID_ where se.deleted = 0
and ( se.apply_user = #{query.applyUser} or se.process_instance_id in (select PROC_INST_ID_ from ACT_HI_TASKINST where ASSIGNEE_ = #{query.applyUser}) )
and art.ASSIGNEE_ = #{applyUser}
and se.process_instance_id in (select PROC_INST_ID_ from ACT_HI_TASKINST where ASSIGNEE_ =#{query.applyUser})
and se.apply_status in (-1 ,7)
and se.name like concat('%',#{query.name},'%')
and se.entrust_type = #{query.entrustType}
and se.apply_status = #{query.applyStatus}
order by se.id desc
